import{_ as o,r as n,o as r,c as a,a as e,b as i,e as l,d as s}from"./app-pwInIdNR.js";const d={},h=e("h1",{id:"题目-http-状态码",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#题目-http-状态码","aria-hidden":"true"},"#"),i(" 题目：http 状态码")],-1),c={id:"常见状态码",tabindex:"-1"},_=e("a",{class:"header-anchor",href:"#常见状态码","aria-hidden":"true"},"#",-1),p={href:"https://developer.mozilla.org/zh-CN/docs/Web/HTTP/Status",target:"_blank",rel:"noopener noreferrer"},u=s('<p>注：加黑表示常见错误码</p><h4 id="_2xx-表示成功" tabindex="-1"><a class="header-anchor" href="#_2xx-表示成功" aria-hidden="true">#</a> 2XX：表示成功</h4><ul><li><p><strong>200（OK）：</strong> 请求成功</p></li><li><p>201（Created）： 请求已成功，并因此创建了一个新资源。通常是在 POST 请求之后返回的响应</p></li><li><p>202（Accepted）： 请求已经接受到，但还未响应，没有结果。</p></li><li><p>203（Non-Authoritative Information）： 非认证信息</p></li><li><p>204（No Content）： 没有内容</p></li><li><p>205（Reset Content）： 重置内容</p></li><li><p><strong>206（Partial Content）：</strong> 请求部分资源，断点续传时返回的状态码</p></li></ul><h4 id="_3xx-表示重定向" tabindex="-1"><a class="header-anchor" href="#_3xx-表示重定向" aria-hidden="true">#</a> 3XX：表示重定向</h4><ul><li>300（Multiple Choices）： 多路选择</li><li><strong>301（Moved Permanently）：</strong> 永久性重定向</li><li><strong>302（Found）：</strong> 临时性重定向</li><li>303（See Other）： 参加其他</li><li><strong>304（Not Modified）：</strong> 未修改，客户端可直接使用缓存</li><li>305（Ues Proxy）： 使用代理</li></ul><h4 id="_4xx-表示客户端错误" tabindex="-1"><a class="header-anchor" href="#_4xx-表示客户端错误" aria-hidden="true">#</a> 4XX：表示客户端错误</h4><ul><li><strong>400（Bad Request）：</strong> 语义有误，或请求参数有误</li><li><strong>401（Unauthorized）：</strong> 未认证</li><li>402（Payment Required）： 需付费</li><li><strong>403（Forbidden）：</strong> 没有权限，被禁止</li><li><strong>404（Not Found）：</strong> 未找到资源，可能地址错误</li><li>405（Method Not Allowed）： 方法不允许</li><li>406（Not Acceptable）： 不接受</li><li>407（Proxy Authentication Required）： 需要代理认证</li><li>408（Request Time-out）： 请求超时</li><li>409（Conflict）： 请求资源发生冲突</li><li>410（Gone）： 失败</li><li>411（Length Required）： 需要长度</li><li>412（Precondition Failed）： 条件失败</li><li>413（Request Entity Too Large）： 请求实体太大</li><li>414（Request-URL Too Large）： 请求 URL 太长</li><li>415（Unsupported Media Type）： 不支持媒体类型</li><li>416（Requested range not satisfiable）： Range 字段指定有误</li><li>417（Expectation Failed）： Expect 字段指定内容无法被服务器满足</li><li>421（There are too many connections from your internet address）： 客户端 IP 地址到服务器的连接数超过可允许范围</li><li>422（Unprocessable Entity）： 请求格式正确，但含有错误语义</li><li>423（Locked）： 当前资源被锁定</li></ul><h4 id="_5xx-表示服务端错误" tabindex="-1"><a class="header-anchor" href="#_5xx-表示服务端错误" aria-hidden="true">#</a> 5XX：表示服务端错误</h4><ul><li><strong>500（Internal Server Error）：</strong> 服务器执行程序过程发生异常</li><li>501（Not Implemented）： 未实现</li><li>502（Bad Gateway）： 网关或代理服务器没有接收到上游服务器的相应</li><li><strong>503（Service Unavailable）：</strong> 服务器崩掉了</li><li>504（Gateway Time-out）： 网关超时</li><li>505（HTTP Version not supported）： 不支持 HTTP 版本</li></ul>',9),x={id:"_301-和-302-区别",tabindex:"-1"},g=e("a",{class:"header-anchor",href:"#_301-和-302-区别","aria-hidden":"true"},"#",-1),f={href:"https://github.com/Advanced-Frontend/Daily-Interview-Question/issues/241",target:"_blank",rel:"noopener noreferrer"},m=e("p",null,"301 是永久性重定向，浏览器接收到这样的响应头时，下次再发起请求时，会直接往新的地址请求，不会再向原地址发请求",-1),b=e("p",null,"302 是临时性重定向，所以浏览器再每次请求时，仍旧会先向原地址发请求",-1);function T(y,N){const t=n("ExternalLinkIcon");return r(),a("div",null,[h,e("h3",c,[_,i(),e("a",p,[i("常见状态码"),l(t)])]),u,e("h3",x,[g,i(),e("a",f,[i("301 和 302 区别"),l(t)])]),m,b])}const v=o(d,[["render",T],["__file","http状态码.html.vue"]]);export{v as default};
